        }
}

def astroCheck()
{
	def s = getSunriseAndSunset(zipCode: zipCode, sunriseOffset: sunriseOffset, sunsetOffset: sunsetOffset)
	state.riseTime = s.sunrise.time
	state.setTime = s.sunset.time
	log.debug "Sunrise: ${new Date(state.riseTime)}($state.riseTime), Sunset: ${new Date(state.setTime)}($state.setTime)"
}

private getDarkOk()
{
	def result
	if (dark == true && lightSensor != null && lightSensor != "")
        {
		result = lightSensor.currentIlluminance < ((luxLevel != null && luxLevel != "") ? luxLevel : 50)
		}
	log.trace "darkOk = $result"
	result
}

private getSunOk()
{
	def result
	if (sun == true)
    	{
		def t = now()
		result = t < state.riseTime || t > state.setTime
		}
	log.trace "sunOk = $result"
	result
}

private getSunriseOffset()
{
	sunriseOffsetValue ? (sunriseOffsetDir == "Before" ? "-$sunriseOffsetValue" : sunriseOffsetValue) : null
}

private getSunsetOffset()
{
	sunsetOffsetValue ? (sunsetOffsetDir == "Before" ? "-$sunsetOffsetValue" : sunsetOffsetValue) : null
}

private getAllOk()
{
	modeOk && daysOk && timeOk
}

private getModeOk()
{
	def result = !modes || modes.contains(location.mode)
	log.trace "modeOk = $result"
	result
}

private getDaysOk()
{
	def result = true
	if (days)
    	{
		def df = new java.text.SimpleDateFormat("EEEE")
		if (location.timeZone)
        	{
			df.setTimeZone(location.timeZone)
			}
		else
        	{
			df.setTimeZone(TimeZone.getTimeZone("America/New_York"))
			}
		def day = df.format(new Date())
		result = days.contains(day)
		}
	log.trace "daysOk = $result"
	result
}

private getTimeOk()
{
	def result = true
	if (starting && ending)
    	{
		def currTime = now()
		def start = timeToday(starting).time
		def stop = timeToday(ending).time
		result = start < stop ? currTime >= start && currTime <= stop : currTime <= stop || currTime >= start
		}
	log.trace "timeOk = $result"
	result
}

private hhmm(time, fmt = "h:mm a")
{
	def t = timeToday(time, location.timeZone)
	def f = new java.text.SimpleDateFormat(fmt)
	f.setTimeZone(location.timeZone ?: timeZone(time))
	f.format(t)
}

private hideOptionsSection()
{
	(starting || ending || days || modes) ? false : true
}

private timeIntervalLabel()
{
	(starting && ending) ? hhmm(starting) + "-" + hhmm(ending, "h:mm a z") : ""
}

