def doorKnock() {
  if((openSensor.latestValue("contact") == "closed") &&
    (now() - (60 * 1000) > state.lastClosed)) {
    def kSensor = knockSensor.label ?: knockSensor.name
    log.debug("${kSensor} detected a knock.")
    send(kSensor)
  }

  else {
