}

private sendMessage(evt) {
	def msg = messageText ?: "We closed the valve because moisture was detected"
	log.debug "$evt.name:$evt.value, pushAndPhone:$pushAndPhone, '$msg'"

	if (!phone || pushAndPhone != "No") {
