	def params = [
		oauth_callback:URLEncoder.encode(callback),
	]
	def requestTokenBaseUrl = "https://oauth.withings.com/account/request_token"
	def url = buildSignedUrl(requestTokenBaseUrl, params)
	//log.debug "getToken - url: $url"

